<script>    $(document).ready(function () {        flatpickr("#daterange", {mode: "range", dateFormat: "d-m-Y"});        new Choices("#status", {removeItemButton: !0});        bindDatatable();        $('#searchsubmit').click(function () {            bindDatatable();        });        $('#addnew').click(function () {            window.location.href = "<?= ADMINPATH ?>user-add";        });    });    function downloaddata() {        var username = $("#name").val();        var email = $("#email").val();        var mobile = $("#mobile").val();        var doj = $("#doj").val();        var status = $("#status").val();        $.ajax({            type: "post",            url: '<?= base_url(); ?>User/downloaduser',            data: {draw: 0, order: [{column: 0, dir: 'ASC'}], username: username, email: email, mobile: mobile, doj: doj, status: status, start: 0, length: 10000},            success: function (result)            {                var obj = JSON.parse(result);                console.log(obj.data);                JSONToCSVConvertor(obj.data, "User_Report-<?= date("d-m-Y_H_i_s") ?>", true);            }        });    }    function bindDatatable(page = 0) {        if (page != 0) {            var page = parseInt(page) * 10;        }        var name = $("#name").val();        var mobile = $("#mobile").val();        var daterange = $("#daterange").val();        var status = $("#status").val();        $('#userlist').DataTable().destroy();        $('#userlist').DataTable({            responsive: true,            searching: false,            processing: true,            ordering: true,            bLengthChange: false,            serverSide: true,            displayStart: page,            pageLength: 10,            order: [[0, 'desc']],            ajax: {                method: "POST",                url: '<?= ADMINPATH ?>users-data',                data: function (d) {                    d.name = name;                    d.mobile = mobile;                    d.daterange = daterange;                    d.status = status;                }            }        });    }    </script>